# Create your models here.from django.db import modelsfrom django.utils import timezoneclass Perfil(models.Model):	nome = models.CharField(max_length=250)	ar_condicionado  = models.IntegerField(default=0)	servo_motor = models.IntegerField(default=0)	radio  = models.BooleanField(default=False)	created = models.DateTimeField(auto_now_add=True)	updated = models.DateTimeField(auto_now=True)	def __unicode__(self):		return self.nome	def __str__(self):		return self.nome	class Meta:		ordering = ["-nome"]